import { storiesOf } from '@storybook/html'

storiesOf('Components/zea-param-widget-vec3', module).add('Default', () => {
  const { Vec3, Vec3Parameter } = globalThis.zeaEngine
  const parameter = new Vec3Parameter('Vec3Parameter', new Vec3(1, 2, 3))

  const paramWidget = document.createElement('zea-param-widget-vec3')
  paramWidget.parameter = parameter

  setTimeout(() => {
    parameter.setValue(new Vec3(4, 5, 6))
  }, 5000)

  if (globalThis.zeaUx) {
    const { UndoRedoManager } = globalThis.zeaUx
    const undoRedoManager = UndoRedoManager.getInstance()

    document.addEventListener('keydown', (e) => {
      if ((e.metaKey || e.ctrlKey) && e.key === 'z') {
        console.log('Undo')
        undoRedoManager.undo()
      } else if ((e.metaKey || e.ctrlKey) && e.key === 'y') {
        console.log('Redo')
        undoRedoManager.redo()
      }
    })
  }

  return paramWidget
})
