import { storiesOf } from '@storybook/html'

storiesOf('Components/zea-param-widget-vec4', module).add('Default', () => {
  const { Vec4, Vec4Parameter } = globalThis.zeaEngine

  const parameter = new Vec4Parameter('Vec4Parameter', new Vec4(1, 2, 3, 4))

  const paramWidget = document.createElement('zea-param-widget-vec4')
  paramWidget.parameter = parameter

  setTimeout(() => {
    parameter.setValue(new Vec4(5, 6, 7, 8))
  }, 5000)

  if (globalThis.zeaUx) {
    const { UndoRedoManager } = globalThis.zeaUx
    const undoRedoManager = UndoRedoManager.getInstance()

    document.addEventListener('keydown', (e) => {
      if ((e.metaKey || e.ctrlKey) && e.key === 'z') {
        undoRedoManager.undo()
      } else if ((e.metaKey || e.ctrlKey) && e.key === 'y') {
        undoRedoManager.redo()
      }
    })
  }

  return paramWidget
})
