import { storiesOf } from '@storybook/html'

storiesOf('Components/zea-param-widget-xfo', module).add('Default', () => {
  const { Xfo, XfoParameter } = globalThis.zeaEngine
  const xfo = new Xfo()

  xfo.tr.set(1, 2, 3)
  xfo.ori.set(4, 5, 6, 7)
  xfo.ori.normalizeInPlace()
  xfo.sc.set(8, 9, 10)

  const parameter = new XfoParameter('XfoParameter', xfo)

  const paramWidget = document.createElement('zea-param-widget-xfo')
  paramWidget.parameter = parameter

  xfo.tr.set(10, 20, 30)
  xfo.ori.set(40, 50, 60, 70)
  xfo.ori.normalizeInPlace()
  xfo.sc.set(80, 90, 100)

  setTimeout(() => {
    parameter.setValue(xfo)
  }, 5000)

  if (globalThis.zeaUx) {
    const { UndoRedoManager } = globalThis.zeaUx
    const undoRedoManager = UndoRedoManager.getInstance()

    document.addEventListener('keydown', (e) => {
      if ((e.metaKey || e.ctrlKey) && e.key === 'z') {
        undoRedoManager.undo()
      } else if ((e.metaKey || e.ctrlKey) && e.key === 'y') {
        undoRedoManager.redo()
      }
    })
  }

  return paramWidget
})
